[
  {
    "id": "4fe8286c0eaf847c920bdaea294f546c823721909f83da08626be599f6bddc90",
    "object": {
      "author": {
        "email": "agent@taskboard.dev",
        "name": "Agent"
      },
      "behavior_spec": [
        {
          "given": "a board ID",
          "then": "return all tasks with matching board_id",
          "when": "tasks are queried for that board"
        },
        {
          "given": "an owner username",
          "then": "return all boards owned by that user",
          "when": "boards are queried"
        }
      ],
      "constraints": [
        "All cross-module queries must use the store APIs, not direct field access"
      ],
      "impacts": [
        "boards",
        "tasks"
      ],
      "parents": [
        "fde3c3ce781c8bf90da70a5b09f3cb727dc070469365cc8ebdcefab944fd108b"
      ],
      "statement": "Enable cross-module queries: tasks by board, boards by owner",
      "timestamp": "2026-02-28T12:10:39.126639Z"
    }
  },
  {
    "id": "fde3c3ce781c8bf90da70a5b09f3cb727dc070469365cc8ebdcefab944fd108b",
    "object": {
      "author": {
        "email": "agent@taskboard.dev",
        "name": "Agent"
      },
      "behavior_spec": [
        {
          "given": "a board with existing tasks",
          "then": "warn about orphaned tasks and require confirmation",
          "when": "delete is requested"
        },
        {
          "given": "a new board",
          "then": "use default columns: Todo, In Progress, Done",
          "when": "created without explicit columns"
        }
      ],
      "constraints": [
        "Deleting a board must cascade-delete or orphan-check its tasks",
        "Board columns define valid task statuses for that board",
        "Board owner has admin privileges over their boards tasks"
      ],
      "impacts": [
        "boards",
        "tasks"
      ],
      "parents": [
        "3f4509c86ebff55ffafd10a1221bc0cbd584b9f528f05179cb913c71a0a08ede"
      ],
      "statement": "Implement board management with cross-module integrity",
      "timestamp": "2026-02-28T12:10:39.089831Z"
    }
  },
  {
    "id": "3f4509c86ebff55ffafd10a1221bc0cbd584b9f528f05179cb913c71a0a08ede",
    "object": {
      "author": {
        "email": "agent@taskboard.dev",
        "name": "Agent"
      },
      "behavior_spec": [
        {
          "given": "a task with status Todo",
          "then": "update succeeds",
          "when": "status is set to InProgress"
        },
        {
          "given": "a task with status Done",
          "then": "return error: backward transition not allowed",
          "when": "status is set to Todo"
        }
      ],
      "constraints": [
        "Status transitions must follow: Todo -> InProgress -> Done",
        "Cannot transition backwards (Done -> Todo) without explicit reset"
      ],
      "impacts": [
        "tasks"
      ],
      "parents": [
        "d66e046c7e96b935ca1cbe7ef196895ec1d105107c75a16128d1c4949d873d92"
      ],
      "statement": "Define task status transition rules",
      "timestamp": "2026-02-28T12:10:39.054270Z"
    }
  },
  {
    "id": "d66e046c7e96b935ca1cbe7ef196895ec1d105107c75a16128d1c4949d873d92",
    "object": {
      "author": {
        "email": "agent@taskboard.dev",
        "name": "Agent"
      },
      "behavior_spec": [
        {
          "given": "a valid CreateTaskRequest with existing board_id",
          "then": "return new Task with status Todo",
          "when": "create is called"
        },
        {
          "given": "a CreateTaskRequest with non-existent board_id",
          "then": "return 400 Bad Request",
          "when": "create is called"
        },
        {
          "given": "a task ID that exists",
          "then": "return the task",
          "when": "get is called"
        },
        {
          "given": "a task ID that does not exist",
          "then": "return 404 Not Found",
          "when": "get is called"
        }
      ],
      "constraints": [
        "Task must reference a valid board_id",
        "Task title must be non-empty and <= 200 characters",
        "Only task assignee or board owner can modify a task"
      ],
      "impacts": [
        "tasks"
      ],
      "parents": [
        "fd84cde9f90848df53cfd893991fff62606137fdf5042d2c60059ec90f5fb5ad"
      ],
      "statement": "Implement Task CRUD operations with validation",
      "timestamp": "2026-02-28T12:10:39.012617Z"
    }
  },
  {
    "id": "fd84cde9f90848df53cfd893991fff62606137fdf5042d2c60059ec90f5fb5ad",
    "object": {
      "author": {
        "email": "agent@taskboard.dev",
        "name": "Agent"
      },
      "behavior_spec": [
        {
          "given": "a Viewer role user",
          "then": "return 403 Forbidden",
          "when": "task creation is attempted"
        },
        {
          "given": "an Admin role user",
          "then": "allow the operation",
          "when": "board deletion is attempted"
        }
      ],
      "constraints": [
        "Admin role can manage boards and users",
        "Member role can create and modify tasks",
        "Viewer role has read-only access"
      ],
      "impacts": [
        "auth",
        "tasks",
        "boards"
      ],
      "parents": [
        "9f3e2a3f448d6283a8cd544e6f2d53983d3088e15bd7454e163b77f6a299adf4"
      ],
      "statement": "Define RBAC roles and permission model",
      "timestamp": "2026-02-28T12:10:38.974204Z"
    }
  },
  {
    "id": "9654ebad9b3d97743817f33926e56832bc9e4b42c2adedfc9bb9474f584adb81",
    "object": {
      "author": {
